Get-ADUser -Filter * -SearchBase "OU=External,OU=Users,OU=_Production,DC=hq,DC=acme,DC=com" | Sort | Select-Object Name,SamAccountName,@{Label="ADAccountEnabled";Expression={$_.Enabled}},DistinguishedName | Export-Csv -Path "C:\Windows\Temp\ad-listing-of-external-users.csv" -NoTypeInformation
Get-ADUser -Filter * -SearchBase "OU=External,OU=Admins,OU=_Production,DC=hq,DC=acme,DC=com" | Sort | Select-Object Name,SamAccountName,@{Label="ADAccountEnabled";Expression={$_.Enabled}},DistinguishedName | Export-Csv -Path "C:\Windows\Temp\ad-listing-of-external-admins.csv" -NoTypeInformation